import axios from 'axios';

export const getMemories = async () => {
let result = await axios.get('http://localhost:3001/api/v1/memories');

return result.data.memories;
}

export const createMemories = async (postData) => {
    console.log("postData",postData);
    let result = await axios.post('http://localhost:3001/api/v1/memories', postData);

    return result.data.memories;
    }

    export const editMemories = async (postData,CurrentId) => {
        console.log("postData",postData);
        let result = await axios.put(`http://localhost:3001/api/v1/memories?id=${CurrentId}`, postData);

        return result.data.memories;
        }
