 const reducer= (state = [], action) => {
    switch (action.type) {
        case 'MEMORIES_FETCH_SUCCESS':
            console.log("state from reducer", state);
        return state;
        case 'MEMORY_ADD_SUCCESS':
        return [...state, action.memory];
        default:
        return state;
    }
}

export default reducer;
